// Function to extract available seats for a specific ride (e.g., 'gidis13')
function getAvailableSeatsForSpecificRide(rideId) {
    const rideDiv = document.getElementById(rideId); // Select the div with the given ride ID
    if (!rideDiv) {
        console.error('Ride not found');
        return { businessSeats: 0, ekonomiSeats: 0 };
    }

    const seatElements = rideDiv.querySelectorAll('span.emptySeat');
    let businessSeats = 0;
    let ekonomiSeats = 0;

    seatElements.forEach(element => {
        const button = element.closest('button');
        const category = button.querySelector('span.text-left').textContent.trim();

        // Check for BUSINESS class
        if (category === 'BUSİNESS') {
            const seatsText = element.textContent.trim();
            businessSeats = parseInt(seatsText.replace(/[^\d]/g, '')); // Extract number from text like "(39)"
        }

        // Check for EKONOMİ class
        if (category === 'EKONOMİ') {
            const seatsText = element.textContent.trim();
            ekonomiSeats = parseInt(seatsText.replace(/[^\d]/g, '')); // Extract number from text like "(415)"
        }
    });

    return { businessSeats, ekonomiSeats };
}


