function clickButtonBasedOnClassInRideDiv(rideId, className) {
    const rideDiv = document.getElementById(rideId);
    console.log('Ride Div:', rideDiv);

    if (!rideDiv) {
        console.log('Ride not found');
        return;
    }

    const buttons = rideDiv.querySelectorAll('button');
    let foundButton = null;

    buttons.forEach(button => {
        const categorySpan = button.querySelector('span.text-left');
        console.log('Category Span:', categorySpan);

        if (categorySpan && categorySpan.textContent.trim().toLowerCase() === className.toLowerCase()) {
            foundButton = button;
        }
    });

    if (foundButton) {
        if (!foundButton.classList.contains('disabled')) {
            foundButton.click();
            console.log(`Clicked the ${className} button.`);

            const selectButton = Array.from(rideDiv.querySelectorAll('button')).find(button => {
                const buttonText = button.textContent.trim();
                console.log('Button Text:', buttonText);
                return buttonText === "Seçin";
            });

            if (selectButton) {
                selectButton.click();
                console.log('Clicked the "Seçin" button.');
            } else {
                console.log('No "Seçin" button found inside the ride div.');
            }
        } else {
            console.log(`The ${className} button is disabled.`);
        }
    } else {
        console.log(`${className} not found in the specified ride div.`);
    }
}


clickButtonBasedOnClassInRideDiv(availableRideId, availableCategory);

