async function waitForWagonButtons() {
    return new Promise((resolve) => {
        const interval = setInterval(() => {
            // Check if there are any wagon buttons
            const wagonButtons = document.querySelectorAll('.wagonMap button');
            if (wagonButtons.length > 0) {
                clearInterval(interval);
                console.log('Wagon buttons loaded, proceeding with selecting seats.');
                resolve();  // Resolve the promise when wagon buttons are found
            }
        }, 1000);  // Retry every second
    });
}

function chooseGenderInPopup() {
    return new Promise((resolve, reject) => {
        // Find the popup that contains the gender options
        const genderButtons = document.querySelectorAll('.popover-body .popoverBtn');
        console.log('Gender Buttons:', genderButtons);

        if (genderButtons.length > 0) {
            // Choose the "Women" option (first button) or "Men" option (second button) based on your requirement
            genderButtons[gender].click(); // Click "Women" button (first button)
            console.log('Clicked the gender button.');
            resolve();
        } else {
            console.log('No gender buttons found in the popup.');
            reject('No gender buttons found');
        }
    });
}

// Function to find and click the first available seat
async function clickFirstAvailableSeat() {
    // Find the section with the seat map
    const seatMapSection = document.querySelector('.row .rotateSeatMap');
    console.log('Seat Map Section:', seatMapSection);

    // Find all the seats inside the seat map
    const seats = seatMapSection.querySelectorAll('.seatMapClick');
    console.log('Seats Found:', seats);

    // Iterate through each seat to check for availability
    for (let seat of seats) {
        // Check if the seat has a valid seat number (and potentially not empty)
        const seatImage = seat.querySelector('img');
        const seatNumber = seat.querySelector('.seatNumber');

        // If the seat has a number and is available (not empty)
        if (((seatImage.src === emptyEkonomiSeatImageUrl) || (seatImage.src === emptyBusinessSeatImageUrl)) && !seatSelected) {
            console.log('Available Seat Found:', seatNumber.textContent);
            // Click the first available seat
            seat.click();
            console.log('Clicked seat:', seatNumber.textContent);
            seatSelected = true;
            await chooseGenderInPopup();
            break;  // Stop once we click the seat
        }
    }

    if (!seatSelected) {
        console.log('No available seats found.');
    }
}

// Function to wait until the seat map is loaded
async function waitForSeatMap() {
    return new Promise((resolve) => {
        const interval = setInterval(() => {
            const seatMapSection = document.querySelector('.row .rotateSeatMap');
            if (seatMapSection && seatMapSection.querySelectorAll('.seatMapClick').length > 0) {
                clearInterval(interval);
                console.log('Seat map loaded, proceeding with selecting seats.');
                resolve();
            }
        }, 50);  // Retry every second
    });
}

// Function to click the available seats in the wagons
async function clickAvailableSeats() {
    // Get all the buttons inside the wagon map section
    const wagonButtons = document.querySelectorAll('.wagonMap button');
    console.log('Found Buttons:', wagonButtons);

    // Iterate over each button to extract seat information and click if seats are available
    for (let button of wagonButtons) {
        // Find the span containing the number of available seats
        const seatCountSpan = button.querySelector('.textEmptySeat span');
        if (seatCountSpan) {
            const availableSeats = parseInt(seatCountSpan.textContent.trim(), 10);  // Convert to integer
            console.log('Available Seats:', availableSeats);

            // Check if seats are available (greater than 0)
            if (availableSeats > 0 && !seatSelected) {
                // Click the button if there are available seats
                button.click();
                console.log('Clicked the button for the wagon with available seats.');

                // Wait for the seat map to load
                await waitForSeatMap();

                // Call the function to click the first available seat
                await clickFirstAvailableSeat();

                if (seatSelected) {
                    console.log('Seat successfully selected. Stopping the process.');
                    break; // Stop after clicking the first available seat
                }
            }
        } else {
            console.log('No seat count found for this button.');
        }
    }

    if (!seatSelected) {
        console.log('No available seats in any of the wagons.');
    }
}

async function main() {
    // Wait for the page to fully load
    await waitForWagonButtons();
    console.log('Page has fully loaded, starting seat selection.');

    // Now, start the process of selecting available seats
    await clickAvailableSeats();
}

// Run the main function
main();
